import { duration } from '@material-ui/core';
import React from 'react';

const ExerciseItem = ({name,exercise,Exercises,setExercises}) => {
      
    const deleteHandler = () => {
        setExercises(Exercises.filter((el) => el.id !== exercise.id));        
    };
    // const completeHandler = () => {
    //     setExercises(Exercises.map(item => {
    //         if(item.id === exercise.id){
    //             return{
    //                 ...item, completed: !item.completed
    //             }
    //         }
    //         return item;
    //     }));
    // }

    return(

        <div className="Exercise">
            <li className="Exercise-item">
                <div className="left">
                    <h2>{exercise.repeats} X {exercise.name}</h2> Duration per set: {exercise.time}  
                </div>
                <div className="right">
                    <button onClick={deleteHandler} className="trash-btn">
                        <i className="fas fa-trash"></i>
                    </button>
                </div>
            </li>

        
            
        </div>

    );
};


export default ExerciseItem;
